import React from "react";
import "../theme.css";

const styles = {
  traitCard: (type) => ({
    display: "flex",
    alignItems: "center",
    width: "150px",
    padding: "12px",
    borderRadius: "8px",
    textAlign: "left",
    fontFamily: "Arial, sans-serif",
    backgroundColor: `var(--${type.toLowerCase()}-bg-color)`,
  }),
  icon: {
    width: "24px",
    height: "24px",
    marginRight: "8px",
    objectFit: "contain",
  },
  text: (type) => ({
    flex: 1,
    color: `var(--${type.toLowerCase()}-color)`,
  }),
};

const getIconSrc = (type) => {
  switch (type) {
    case "Virtue":
      return "https://via.placeholder.com/24/4caf50?text=V";
    case "Vice":
      return "https://via.placeholder.com/24/f44336?text=VI";
    case "Trade":
      return "https://via.placeholder.com/24/2196f3?text=T";
    case "Tidbit":
      return "https://via.placeholder.com/24/ff9800?text=TI";
    default:
      return "https://via.placeholder.com/24/cccccc?text=?";
  }
};

const TraitCard = ({ type, text }) => (
  <div style={styles.traitCard(type)}>
    <img src={getIconSrc(type)} alt={`${type} icon`} style={styles.icon} />
    <p style={styles.text(type)}>{text}</p>
  </div>
);

export default TraitCard;
