import React from "react";
import TraitCard from "./Trait";
import TraitStack from "./TraitStack";
import "../theme.css";

const styles = {
  character: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: "16px",
  },
  characterImage: {
    maxWidth: "var(--desktop-character-width)",
    boxShadow: "var(--card-shadow)",
    borderRadius: "8px",
  },
  characterText: {
    fontFamily: "Candy Darling, sans-serif",
    fontSize: "104px",
    color: "var(--virtue-color)",
    marginBottom: "-100px",
    zIndex: 10,
    textShadow: `
      -2.03px 2.03px 1.35px #FFD6E8, 
      2.03px -2.03px 1.35px #FFD6E8,
      inset 0px 0px 2.35px #2B1A21
    `,
  },
  traitsWrapper: {
    marginTop: -50,
  },
};

const Character = ({ image, traits, dir }) => {
  const getRandomRotation = (dir) => {
    const rotation = Math.random() * 10;
    return dir === "left" ? -1 * rotation : rotation;
  };
  const rotation = getRandomRotation();
  return (
    <div
      style={{
        transform: `rotate(${rotation}deg)`,
        ...styles.character,
      }}
    >
      <div style={styles.characterText}>Troye</div>
      <img
        src={"img/boys/finn.png"}
        alt="Character"
        style={styles.characterImage}
      />

      <div style={styles.traitsWrapper}>
        <TraitStack traits={traits} />
      </div>
    </div>
  );
};

export default Character;
