import React, { useMemo } from "react";
import "../animations.css";

const Player = ({ image, name, isActive }) => {
  const randomRotation = useMemo(() => {
    const range = Math.random() < 0.5 ? [-20, -10] : [10, 20]; // Choose between the two ranges
    return Math.random() * (range[1] - range[0]) + range[0];
  }, []);
  const randomYShift = useMemo(() => Math.random() * 20 - 10, []);

  const animationDuration = useMemo(() => Math.random() * 2 + 3, []); // Duration between 3s and 5s
  const animationDelay = useMemo(() => Math.random() * 2, []); // Delay between 0s and 2s

  const styles = {
    container: {
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      width: "120px",
      transform: `translateY(${randomYShift}px) rotate(${randomRotation}deg)`,
      animation: `drift ${animationDuration}s ease-in-out ${animationDelay}s infinite`, // Unique animation per character
      // animation: "drift 5s ease-in-out infinite",
      transition: "filter 0.3s ease, opacity 0.3s ease",
      margin: "0px 25px",
      marginBottom: "50px",
    },
    image: {
      maxWidth: "70%",
      aspectRatio: "1 / 1",
      // width: "80px",
      // height: "80px",
      borderRadius: "50%",
      objectFit: "cover",
      filter: isActive ? "none" : "grayscale(100%)",
      opacity: isActive ? 1 : 0.5,
    },
    name: {
      marginTop: "10px",
      fontSize: "16px",
      fontWeight: "bold",
      textAlign: "center",
      color: isActive ? "white" : "#333",
    },
  };

  return (
    <div style={styles.container}>
      <img src={image} alt={name} style={styles.image} />
      <div style={styles.name}>{name}</div>
    </div>
  );
};

export default Player;
