import React from "react";
import TraitCard from "./Trait";
import TraitStack from "./TraitStack";
import "../theme.css";

const styles = {
  character: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: "16px",
  },
  characterImage: {
    maxWidth: "var(--desktop-character-width)",
    borderRadius: "8px",
  },
  traitsWrapper: {
    display: "flex",
    flexDirection: "column",
    gap: "12px",
    traitsWrapper: {
      position: "relative", // Relative positioning to contain absolute-positioned cards
      width: "180px", // Match card width + some spacing
      height: "300px", // Enough height to accommodate staggered cards
    },
    alignItems: "center",
    width: "100%",
  },
};

const Character = ({ image, virtue, vice, trade, tidbit }) => (
  <div style={styles.character}>
    <img
      src={"img/boys/finn.png"}
      alt="Character"
      style={styles.characterImage}
    />
    <TraitStack
      traits={[
        { text: virtue, type: "Virtue" },
        { text: vice, type: "Vice" },
        { text: trade, type: "Trade" },
        { text: tidbit, type: "Tidbit" },
      ]}
    />
    {/* <div style={styles.traitsWrapper}>
      <TraitCard type="Virtue" text={virtue} />
      <TraitCard type="Vice" text={vice} />
      <TraitCard type="Trade" text={trade} />
      <TraitCard type="Tidbit" text={tidbit} />
    </div> */}
  </div>
);

export default Character;
