import React from "react";

const getBackgroundColor = (type) => {
  switch (type) {
    case "Virtue":
      return "#4caf50";
    case "Vice":
      return "#f44336";
    case "Trade":
      return "#2196f3";
    case "Tidbit":
      return "#ff9800";
    default:
      return "#ccc";
  }
};

const styles = {
  character: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: "16px",
  },
  characterImage: {
    width: "200px",
    height: "200px",
    objectFit: "cover",
    borderRadius: "50%",
    border: "4px solid #ccc",
  },
  traitsWrapper: {
    display: "flex",
    gap: "12px",
    flexWrap: "wrap",
  },
  traitCard: (type) => ({
    width: "150px",
    padding: "12px",
    borderRadius: "8px",
    color: "#fff",
    textAlign: "center",
    fontFamily: "Arial, sans-serif",
    backgroundColor: getBackgroundColor(type),
  }),
};

const TraitCard = ({ type, text }) => (
  <div style={styles.traitCard(type)}>
    <span>{type}</span>
    <p>{text}</p>
  </div>
);

const Character = ({ image, virtue, vice, trade, tidbit }) => (
  <div style={styles.character}>
    <img src={image} alt="Character" style={styles.characterImage} />
    <div style={styles.traitsWrapper}>
      <TraitCard type="Virtue" text={virtue} />
      <TraitCard type="Vice" text={vice} />
      <TraitCard type="Trade" text={trade} />
      <TraitCard type="Tidbit" text={tidbit} />
    </div>
  </div>
);

export default Character;
