import React, { useMemo } from "react";

const Player = ({ image, name, isActive }) => {
  const randomRotation = useMemo(() => Math.random() * 30 - 10, []);
  const randomYShift = useMemo(() => Math.random() * 20 - 10, []);

  const styles = {
    container: {
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      width: "120px",
    },
    image: {
      width: "100px",
      height: "100px",
      borderRadius: "50%",
      objectFit: "cover",
      filter: isActive ? "none" : "grayscale(100%)",
      opacity: isActive ? 1 : 0.5,
      transform: `translateY(${randomYShift}px) rotate(${randomRotation}deg)`,
      animation: "drift 5s ease-in-out infinite",
      transition: "filter 0.3s ease, opacity 0.3s ease", // Smooth transition between active/inactive states
    },
    name: {
      marginTop: "10px",
      fontSize: "16px",
      fontWeight: "bold",
      textAlign: "center",
      color: isActive ? "#333" : "#888",
    },
  };

  return (
    <div style={styles.container}>
      <img src={image} alt={name} style={styles.image} />
      <div style={styles.name}>{name}</div>
    </div>
  );
};

export default Player;
