import React from "react";
import "../theme.css";

const getIconSrc = (type) => {
  switch (type) {
    case "Virtue":
      return "https://via.placeholder.com/24/4caf50?text=V";
    case "Vice":
      return "https://via.placeholder.com/24/f44336?text=VI";
    case "Trade":
      return "https://via.placeholder.com/24/2196f3?text=T";
    case "Tidbit":
      return "https://via.placeholder.com/24/ff9800?text=TI";
    default:
      return "https://via.placeholder.com/24/cccccc?text=?";
  }
};

const styles = {
  character: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: "16px",
  },
  characterImage: {
    width: "200px",
    height: "200px",
    objectFit: "cover",
    border: "4px solid #ccc",
  },
  traitsWrapper: {
    display: "flex",
    flexDirection: "column",
    gap: "12px",
    alignItems: "center",
    width: "100%",
  },
  traitCard: (type) => ({
    display: "flex",
    alignItems: "center",
    width: "150px",
    padding: "12px",
    borderRadius: "8px",
    textAlign: "left",
    fontFamily: "Arial, sans-serif",
    backgroundColor: `var(--${type.toLowerCase()}-bg-color)`,
  }),
  icon: {
    width: "24px",
    height: "24px",
    marginRight: "8px",
    objectFit: "contain",
  },
  text: (type) => ({
    flex: 1,
    color: `var(--${type.toLowerCase()}-color)`,
  }),
};

const TraitCard = ({ type, text }) => (
  <div style={styles.traitCard(type)}>
    <img src={getIconSrc(type)} alt={`${type} icon`} style={styles.icon} />
    <p style={styles.text(type)}>{text}</p>
  </div>
);

const Character = ({ image, virtue, vice, trade, tidbit }) => (
  <div style={styles.character}>
    <img src={image} alt="Character" style={styles.characterImage} />
    <div style={styles.traitsWrapper}>
      <TraitCard type="Virtue" text={virtue} />
      <TraitCard type="Vice" text={vice} />
      <TraitCard type="Trade" text={trade} />
      <TraitCard type="Tidbit" text={tidbit} />
    </div>
  </div>
);

export default Character;
